import React, { useState, useEffect } from 'react';
import image from '../assets/Vector_tri.png';
import image1 from '../assets/Vector_trr.png';
import './Stopwatch.css';
import { Howl } from 'howler';
// import sound from '../songs/'
// import './CountdownTimer.css';
// import Counter from './Counter';
// import { useNavigate } from "react-router-dom";
import { CountdownCircleTimer } from 'react-countdown-circle-timer'
// let audioElement = new Audio('../assets/10.mp3');
// import React { useState, useEffect } from 'react';


function Stopwatch() {
  const [isRunning, setIsRunning] = useState(false);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);


  const totalSeconds = hours * 3600 + minutes * 60 + seconds;

  useEffect(() => {
    let interval;

    if (isRunning && totalSeconds > 0) {
      interval = setInterval(() => {
        const remainingSeconds = totalSeconds -1  ;
        setHours(Math.floor(remainingSeconds / 3600));
        setMinutes(Math.floor((remainingSeconds % 3600) / 60));
        setSeconds(remainingSeconds % 60);
      }, 1000);

    } else {
      clearInterval(interval);
    }
    
    return () => clearInterval(interval);
  }, [isRunning, totalSeconds]);

  const toggleRunning = () => {
    setIsRunning(!isRunning);
    
  };
 
  useEffect(()=>{
    if(isRunning){
      if(hours===0 && minutes===0 && seconds===0){
        fetch(audioURL)
        .then((response) => response.arrayBuffer())
        .then((data) => audioContext.decodeAudioData(data))
        .then((buffer) => {
          // The audio has been successfully decoded
          const source = audioContext.createBufferSource();
          source.buffer = buffer;
          source.connect(audioContext.destination);
          source.start(0);
        })
        .catch((error) => {
          console.error('Error decoding audio:', error);
        });
        console.log('KOL')
        setIsRunning(false)
      }
      }
  })
 

  const resetTimer = () => {
    setHours(0);
    setMinutes(0);
    setSeconds(0);
    setIsRunning(false);
  };
  const [background, setBackground] = useState('#191E39');
  
  return (
    <div>

      <div className='Mu'>

      <div className='cir'>
        <CountdownCircleTimer
        // isRunning
        isPlaying={isRunning}
        duration={totalSeconds}
        colors={[ '#FF6A6A']}
        trailColor="transparent"
        rotation="counterclockwise"
        size={170}>


          {({ remainingTime , animatedColor}) => {
        
          return (
            <div>
               <div style={{ color: animatedColor }}>
              
                  {hours}h {minutes}m {seconds}s
           
            </div>
            </div>
          );
        }}
        </CountdownCircleTimer>
      </div>


      <div>
      
        <div className="hs">
          
          <div className='Hou'>
            <div>Hours</div>
            <img src={image} onClick={() => setHours(hours + 1)} className='siz'></img>
            <div>{hours}</div>
            <img src={image1} onClick={() => setHours(hours - 1)} className='siz'></img>
          </div>

          <div className='Hou'>
            <div>Minutes</div>
            <img src={image} onClick={() => setMinutes(minutes + 1)} className='siz'></img>
            <div>{minutes}</div>
            <img src={image1} onClick={() => setMinutes(minutes - 1)} className='siz'></img>
          </div>

          <div className='Hou'>
            <div>seconds</div>
            <img src={image} onClick={() => setSeconds(seconds + 1)} className='siz'></img>
            <div>{seconds}</div>
            <img src={image1} onClick={() => setSeconds(seconds - 1)} className='siz'></img>
          </div>

        </div>

        <div>      {isRunning && <button onClick={resetTimer} className='start'>Pause</button>}
      {!isRunning && <button onClick={toggleRunning} className='start'>Start</button>}</div>
      </div>

      </div>

      
    </div>
  );
}

export default Stopwatch;


