import React, { useState, useEffect } from 'react';
import image from '../assets/Vector_tri.png';
import image1 from '../assets/Vector_trr.png';
import './Stopwatch.css'
import Counter from './Counter';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'
let audioElement = new Audio('../assets/10.mp3');

// import React { useState, useEffect } from 'react';

function Stopwatch() {
  const [isRunning, setIsRunning] = useState(false);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

//   const formatTime = (h, m, s) => {
//     return `${h}h ${m}m ${s}s`;
//   };

  const totalSeconds = hours * 3600 + minutes * 60 + seconds;

  useEffect(() => {
    let interval;

    if (isRunning && totalSeconds > 0) {
      interval = setInterval(() => {
        const remainingSeconds = totalSeconds - 1;
        setHours(Math.floor(remainingSeconds / 3600));
        setMinutes(Math.floor((remainingSeconds % 3600) / 60));
        setSeconds(remainingSeconds % 60);
      }, 1000);

    } else {
      clearInterval(interval);
    }
    
    return () => clearInterval(interval);
  }, [isRunning, totalSeconds]);

  const toggleRunning = () => {
    setIsRunning(!isRunning);
    
  };
  const audioFilePath = '/audio/audio-file.mp3';
  useEffect(()=>{
    if(isRunning){
      if(hours===0 , minutes===0, seconds===0){
        // audioFilePath.play();
        const toggleAudio = () => {
          const audioElement = audioRef.current;
      
          if (isPlaying) {
            audioElement.pause();
          } else {
            audioElement.play();
          }
      
          setIsPlaying(!isPlaying);
        };
        setIsRunning(false)
      }
    }
  })
 

  const resetTimer = () => {
    setHours(0);
    setMinutes(0);
    setSeconds(0);
    setIsRunning(false);
  };
  

  
  return (
    <div className='pause'>
      <div className='Mu' >

  
        <div className='stopWatc'>

        <CountdownCircleTimer
      isRunning
      
      duration={totalSeconds} 
      colors={['#004777', '#F7B801', '#A30000', '#A30000']}
      colorsTime={[7, 5, 2, 0]}
      
      onComplete={()=>{console.log("Hell")}}
    >
      
      {({ remainingTime }) => (
        <div>
        <div style={{ fontSize: 24 }}>{Math.floor(remainingTime / 3600).toString().padStart(2, '0')}:</div>
        <div style={{ fontSize: 24 }}>{Math.floor((remainingTime % 3600) / 60).toString().padStart(2, '0')}:</div>
        <div style={{ fontSize: 24 }}>{Math.floor(remainingTime % 60).toString().padStart(2, '0')}</div>
      </div>
      )}
    
    </CountdownCircleTimer>
        </div>
      
        <div className="hs">
          
          <div>
            <div>Hours</div>
            <img src={image} onClick={() => setHours(hours + 1)}></img>
            <div>{hours}</div>
            <img src={image1} onClick={() => setHours(hours - 1)}></img>
          </div>

          <div>
            <div>Minutes</div>
            <img src={image} onClick={() => setMinutes(minutes + 1)}></img>
            <div>{minutes}</div>
            <img src={image1} onClick={() => setMinutes(minutes - 1)}></img>
          </div>

          <div>
            <div>seconds</div>
            <img src={image} onClick={() => setSeconds(seconds + 1)}></img>
            <div>{seconds}</div>
            <img src={image1} onClick={() => setSeconds(seconds - 1)}></img>
          </div>

        </div>


      </div>


      {isRunning && <button onClick={resetTimer}>Pause</button>}
      {!isRunning && <button onClick={toggleRunning}>Start</button>}
    </div>
  );
}

export default Stopwatch;
