import React, { useState, useEffect } from 'react';
import image from '../assets/Vector_tri.png';
import image1 from '../assets/Vector_trr.png'
function Stopwatch() {
  const [isRunning, setIsRunning] = useState(false);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

//   const formatTime = (h, m, s) => {
//     return `${h}h ${m}m ${s}s`;
//   };

  const totalSeconds = hours * 3600 + minutes * 60 + seconds;

  useEffect(() => {
    let interval;

    if (isRunning && totalSeconds > 0) {
      interval = setInterval(() => {
        const remainingSeconds = totalSeconds - 1;
        setHours(Math.floor(remainingSeconds / 3600));
        setMinutes(Math.floor((remainingSeconds % 3600) / 60));
        setSeconds(remainingSeconds % 60);
      }, 1000);
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isRunning, 
  ]);

  const toggleRunning = () => {
    setIsRunning(!isRunning);
  };

  const resetTimer = () => {
    setHours(0);
    setMinutes(0);
    setSeconds(0);
    setIsRunning(false);
  };

  return (
    <div>
      <div className='hs'>
        {/* {formatTime(hours, minutes, seconds)} */}
        {/* {minutes} : {seconds} */}
        <div>
          <div>Hours</div>
          <img src={image}></img>
          <div>{hours}</div>
          <img src={image1}></img>
        </div>
        <div>
          <div>Minutes</div>
          <img src={image}></img>
          <div>{minutes}</div>
          <img src={image1}></img>
        </div>
        <div>
          <div>seconds</div>
          <img src={image}></img>
          <div>{seconds}</div>
          <img src={image1}></img>
        </div>
      </div>
      <button onClick={toggleRunning}>{isRunning ? "Pause" : "Start"}</button>
      <button onClick={resetTimer}>Reset</button>
      <div>
        <button onClick={() => setHours(hours + 1)}>Increment Hours</button>
        <button onClick={() => setHours(hours - 1)}>Decrement Hours</button>
      </div>
      <div>
        <button onClick={() => setMinutes(minutes + 1)}>
          Increment Minutes
        </button>
        <button onClick={() => setMinutes(minutes - 1)}>
          Decrement Minutes
        </button>
      </div>
      <div>
        <button onClick={() => setSeconds(seconds + 1)}>
          Increment Seconds
        </button>
        <button onClick={() => setSeconds(seconds - 1)}>
          Decrement Seconds
        </button>
      </div>
    </div>
  );
}

export default Stopwatch;
